import axios from '../../../node_modules/axios'
import {navBar} from "../../store";

export default {

    name: "login",
    beforeCreate: function () {
        if (this.$session.exists()) {
            this.$router.push('/adminPanel')
        }
    },
    data() {
        return {
            showDismissibleAlert: false,
            logged: "",
            input: {
                login: "",
                pwd:  ""
            }
        }
    },
    methods: {
        auth()
        {
            if(this.input.login === "" || this.input.pwd === "")
            {
                this.showDismissibleAlert = true
            }
            else
            {
                axios.post(`http://localhost:3000/auth`, {
                    body: this.input
                })
                    .then(response => {
                        this.logged = response.data;
                        if( response.data == 1)
                        {
                            this.$session.set('login', this.input.login);
                            for(let i = 0;i < navBar.navData.length;i++)
                            {
                                if(navBar.navData[i].page === "Connexion")
                                {
                                    navBar.navData.splice(i,1);
                                    navBar.navData.push({
                                        page: "Deconnexion",
                                        page_url: '/logout'
                                    });
                                }
                            }
                            this.$router.push('/home');

                        }
                        else if(response.data == 2)
                        {
                            this.$session.set('login', this.input.login);
                            this.$session.set('admin', 1);
                            for(let i = 0;i < navBar.navData.length;i++)
                            {
                                if(navBar.navData[i].page === "Connexion")
                                {
                                    navBar.navData.splice(i,3);
                                    navBar.navData.push({
                                        page: "Deconnexion",
                                        page_url: '/logout'
                                    }, {
                                            page: "Administration",
                                            page_url: '/login'
                                    });
                                }
                            }
                            this.$router.push('/adminPanel');
                        }
                        else
                        {
                            this.showDismissibleAlert = true
                        }

                    })
                    .catch(e => {
                        this.errors.push(e)
                    })
            }
        }
    }


}
